        return new Source("{% extends 'admin/base.html.twig' %}

{% block title %}Connexion Administration {% endblock %}


{% block body %}
<div class=\"col-md-4 mx-auto mt-5\">

    <div class=\"car bg-light\">
        <div class=\"card-header\">

            <h4 class=\"card-title\"><i class=\"fas fa-lock\"> Connexion à l'administration</i></h4>
        </div>

        {% if error %}
        <span>Les informations sont incorrects !</span>
        {% endif %}

        <div class=\"card-body\">
            <form action=\"\" method=\"post\">

                <div class=\"form-group\"><label for=\"_username\"></label><input type=\"text\" class=\"form-controle\"
                        placeholder=\"Adresse email\" name=\"_username\">{{lastname}}</div>
                <div class=\"form-group\"><label for=\"_password\"></label><input type=\"password\" class=\"form-controle\"
                        placeholder=\"Mot de passe\" name=\"_password\"></div>

                <div class=\"form-group\"><button type=\"submit\" class=\"btn btn-primary\">
                        <i class=\"fas fa-lock-open\"></i> Connexion
                    </button>
                    <a href=\"{{path('annonces')}}\" class=\"btn btn-link\"> <i class=\"fas fa-arrow-circle-left\"></i> Retour
                        au
                        site</a>
                </div>
            </form>


        </div>
    </div>


</div>

{% endblock %}", "admin/account/login.html.twig", "C:\\Users\\walid\\Desktop\\symfony_airbnb\\templates\\admin\\account\\login.html.twig");
